import axios from 'axios';
import { 
  UserProfile, 
  Skill, 
  SwapRequest, 
  AuthResponse, 
  LoginForm, 
  RegisterForm, 
  SkillFormType, 
  SwapRequestForm,
  Chat,
  Message,
  MessageForm,
  Meeting,
  MeetingForm,
  Notification
} from '../types';

const API_BASE_URL = 'http://localhost:8000/api';

// Configure axios to include auth token
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Token ${token}`;
  }
  return config;
});

// User API
export const userAPI = {
  register: async (data: { username: string; email: string; password: string }) => {
    const response = await axios.post(`${API_BASE_URL}/users/register/`, data);
    return response.data;
  },
  
  login: async (data: { username: string; password: string }) => {
    const response = await axios.post(`${API_BASE_URL}/users/login/`, data);
    return response.data;
  },
  
  getProfile: async () => {
    const response = await axios.get(`${API_BASE_URL}/users/me/`);
    return response.data;
  },
};

// Auth API (alias for userAPI)
export const authAPI = userAPI;

// Profiles API (alias for userAPI)
export const profilesAPI = userAPI;

// Skill API
export const skillsAPI = {
  getAll: () => axios.get(`${API_BASE_URL}/skills/`),
  getSkills: (params?: any) => axios.get(`${API_BASE_URL}/skills/`, { params }),
  create: (data: { name: string; description: string }) =>
    axios.post(`${API_BASE_URL}/skills/`, data),
  createSkill: (data: { name: string; description: string }) =>
    axios.post(`${API_BASE_URL}/skills/`, data),
  update: (id: number, data: any) =>
    axios.put(`${API_BASE_URL}/skills/${id}/`, data),
  delete: (id: number) => axios.delete(`${API_BASE_URL}/skills/${id}/`),
  deleteSkill: (id: number) => axios.delete(`${API_BASE_URL}/skills/${id}/`),
};

// Swap Request API
export const swapRequestsAPI = {
  getAll: () => axios.get(`${API_BASE_URL}/swap-requests/`),
  create: (data: { to_user: number; from_skill: number; to_skill: number; message?: string }) =>
    axios.post(`${API_BASE_URL}/swap-requests/`, data),
  accept: (id: number) => axios.post(`${API_BASE_URL}/swap-requests/${id}/accept/`),
  reject: (id: number) => axios.post(`${API_BASE_URL}/swap-requests/${id}/reject/`),
};

// Swaps API (alias for swapRequestsAPI)
export const swapsAPI = swapRequestsAPI;

// Chat API
export const chatsAPI = {
  getAll: () => axios.get(`${API_BASE_URL}/chats/`),
  getChats: () => axios.get(`${API_BASE_URL}/chats/`),
  getMessages: (chatId: number) => axios.get(`${API_BASE_URL}/messages/?chat_id=${chatId}`),
  createMessage: (data: { chat: number; content: string }) =>
    axios.post(`${API_BASE_URL}/messages/`, data),
};

// Meeting API
export const meetingsAPI = {
  getAll: () => axios.get(`${API_BASE_URL}/meetings/`),
  getMeetings: () => axios.get(`${API_BASE_URL}/meetings/`),
  create: (data: any) => axios.post(`${API_BASE_URL}/meetings/`, data),
  createMeeting: (data: any) => axios.post(`${API_BASE_URL}/meetings/`, data),
  start: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/start/`),
  startMeeting: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/start/`),
  join: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/join/`),
  joinMeeting: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/join/`),
  end: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/end/`),
  cancel: (id: number) => axios.post(`${API_BASE_URL}/meetings/${id}/cancel/`),
};

// Messages API
export const messagesAPI = {
  getMessages: (chatId: number) => axios.get(`${API_BASE_URL}/messages/?chat_id=${chatId}`),
  createMessage: (data: { chat: number; content: string }) =>
    axios.post(`${API_BASE_URL}/messages/`, data),
};

// Notification API
export const notificationsAPI = {
  getAll: () => axios.get(`${API_BASE_URL}/notifications/`),
  markAsRead: (id: number) => axios.post(`${API_BASE_URL}/notifications/${id}/mark_as_read/`),
  markAllAsRead: () => axios.post(`${API_BASE_URL}/notifications/mark_all_as_read/`),
  getUnreadCount: () => axios.get(`${API_BASE_URL}/notifications/unread_count/`),
  clearAll: () => axios.delete(`${API_BASE_URL}/notifications/clear_all/`),
};
