import React, { useState, useEffect } from 'react';
import { Meeting, Notification } from '../types';

interface NotificationPanelProps {
  notifications: Notification[];
  onJoinMeeting: (meeting: Meeting) => void;
  onDismissNotification: (notificationId: number) => void;
  onMarkAsRead: (notificationId: number) => void;
  onClearAll: () => void;
  onClose: () => void;
}

const NotificationPanel: React.FC<NotificationPanelProps> = ({
  notifications,
  onJoinMeeting,
  onDismissNotification,
  onMarkAsRead,
  onClearAll,
  onClose
}) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Show panel when there are notifications
    if (notifications.length > 0) {
      setIsVisible(true);
    }
  }, [notifications]);

  const handleJoin = (meeting: Meeting) => {
    onJoinMeeting(meeting);
    // Mark all notifications for this meeting as read
    notifications
      .filter(n => n.related_meeting === meeting.id)
      .forEach(n => onMarkAsRead(n.id));
  };

  const handleDismiss = (notificationId: number) => {
    onDismissNotification(notificationId);
  };

  const handleMarkAsRead = (notificationId: number) => {
    onMarkAsRead(notificationId);
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'MEETING_STARTED':
        return '🚀';
      case 'MEETING_REMINDER':
        return '⏰';
      case 'MEETING_ENDED':
        return '✅';
      case 'SWAP_REQUEST':
        return '🤝';
      case 'SWAP_ACCEPTED':
        return '✅';
      case 'SWAP_REJECTED':
        return '❌';
      default:
        return '📢';
    }
  };

  const getNotificationTitle = (notification: Notification) => {
    return notification.title;
  };

  const formatTimeAgo = (timestamp: string) => {
    const now = new Date();
    const notificationTime = new Date(timestamp);
    const diffInSeconds = Math.floor((now.getTime() - notificationTime.getTime()) / 1000);
    
    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h ago`;
    return `${Math.floor(diffInSeconds / 86400)}d ago`;
  };

  if (!isVisible || notifications.length === 0) return null;

  return (
    <div style={{
      position: 'fixed',
      top: '20px',
      right: '20px',
      width: '400px',
      maxHeight: '600px',
      backgroundColor: 'white',
      borderRadius: '10px',
      boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
      zIndex: 10000,
      overflow: 'hidden',
      border: '1px solid #ddd'
    }}>
      {/* Header */}
      <div style={{
        backgroundColor: '#007bff',
        color: 'white',
        padding: '15px 20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderTopLeftRadius: '10px',
        borderTopRightRadius: '10px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <span style={{ fontSize: '18px' }}>🔔</span>
          <span style={{ fontWeight: 'bold' }}>Notifications</span>
          <span style={{
            backgroundColor: 'rgba(255,255,255,0.2)',
            borderRadius: '50%',
            padding: '2px 8px',
            fontSize: '12px',
            minWidth: '20px',
            textAlign: 'center'
          }}>
            {notifications.length}
          </span>
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={onClearAll}
            style={{
              background: 'rgba(255,255,255,0.2)',
              border: 'none',
              color: 'white',
              fontSize: '12px',
              cursor: 'pointer',
              padding: '4px 8px',
              borderRadius: '4px'
            }}
            title="Clear all notifications"
          >
            Clear All
          </button>
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              color: 'white',
              fontSize: '20px',
              cursor: 'pointer',
              padding: '0'
            }}
          >
            ×
          </button>
        </div>
      </div>

      {/* Notifications List */}
      <div style={{
        maxHeight: '500px',
        overflowY: 'auto',
        padding: '10px 0'
      }}>
        {notifications.length === 0 ? (
          <div style={{
            padding: '20px',
            textAlign: 'center',
            color: '#666',
            fontSize: '14px'
          }}>
            No notifications
          </div>
        ) : (
          notifications.map((notification) => (
            <div
              key={notification.id}
              style={{
                padding: '15px 20px',
                borderBottom: '1px solid #eee',
                backgroundColor: notification.read ? 'white' : '#f8f9fa',
                position: 'relative'
              }}
            >
              {/* Unread indicator */}
              {!notification.read && (
                <div style={{
                  position: 'absolute',
                  top: '15px',
                  left: '8px',
                  width: '8px',
                  height: '8px',
                  backgroundColor: '#007bff',
                  borderRadius: '50%'
                }} />
              )}

              <div style={{ display: 'flex', alignItems: 'flex-start', gap: '12px' }}>
                <div style={{ fontSize: '20px', marginTop: '2px' }}>
                  {getNotificationIcon(notification.notification_type)}
                </div>
                
                <div style={{ flex: 1 }}>
                  <div style={{
                    fontWeight: 'bold',
                    fontSize: '14px',
                    marginBottom: '5px',
                    color: notification.read ? '#666' : '#333'
                  }}>
                    {getNotificationTitle(notification)}
                  </div>
                  
                  <div style={{
                    fontSize: '13px',
                    color: '#666',
                    marginBottom: '8px',
                    lineHeight: '1.4'
                  }}>
                    {notification.message}
                  </div>
                  
                  <div style={{
                    fontSize: '11px',
                    color: '#999',
                    marginBottom: '10px'
                  }}>
                    {formatTimeAgo(notification.created_at)}
                  </div>
                  
                  {/* Action buttons */}
                  <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                    {notification.notification_type === 'MEETING_STARTED' && notification.related_meeting && (
                      <button
                        onClick={() => {
                          // Create a mock meeting object for the join action
                          const meeting: Meeting = {
                            id: notification.related_meeting || 0,
                            title: notification.meeting_title || 'Meeting',
                            description: notification.message,
                            organizer: 0,
                            organizer_name: '',
                            chat: 0,
                            scheduled_at: undefined,
                            duration_minutes: 30,
                            status: 'ONGOING',
                            created_at: notification.created_at
                          };
                          handleJoin(meeting);
                        }}
                        style={{
                          background: '#28a745',
                          color: 'white',
                          border: 'none',
                          padding: '4px 12px',
                          borderRadius: '4px',
                          fontSize: '12px',
                          cursor: 'pointer'
                        }}
                      >
                        Join Meeting
                      </button>
                    )}
                    
                    {!notification.read && (
                      <button
                        onClick={() => handleMarkAsRead(notification.id)}
                        style={{
                          background: '#6c757d',
                          color: 'white',
                          border: 'none',
                          padding: '4px 12px',
                          borderRadius: '4px',
                          fontSize: '12px',
                          cursor: 'pointer'
                        }}
                      >
                        Mark Read
                      </button>
                    )}
                    
                    <button
                      onClick={() => handleDismiss(notification.id)}
                      style={{
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        padding: '4px 12px',
                        borderRadius: '4px',
                        fontSize: '12px',
                        cursor: 'pointer'
                      }}
                    >
                      Dismiss
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default NotificationPanel;
